Arduino Uno Code:
```
// Sequential LED blink
// This code interfaces with a circuit that has four LEDS (D1-D4) that blink in sequence, one after the other, 
// every 500ms. When the end of the sequence is reached, the pattern repeats.

// LED 1 on Digital I/O 2
#define PIN_LED1 2  
// LED 2 on Digital I/O 3
#define PIN_LED2 3
// LED 3 on Digital I/O 4 
#define PIN_LED3 4
// LED 4 on Digital I/O 5
#define PIN_LED4 5

// the setup function runs once when you press reset or power the board
void setup() {
    // Initialize LED pins to output mode
    pinMode(PIN_LED1, OUTPUT);  
    pinMode(PIN_LED2, OUTPUT);
    pinMode(PIN_LED3, OUTPUT);
    pinMode(PIN_LED4, OUTPUT);
}

// the loop function runs over and over again forever
void loop() {
    //Turn on LED 1
    digitalWrite(PIN_LED1, HIGH);     
    //Wait 500ms
    delay(500);                      
    //Turn off LED 1
    digitalWrite(PIN_LED1, LOW);      
    //Turn on LED 2
    digitalWrite(PIN_LED2, HIGH);     
    //Wait 500ms
    delay(500);                      
    //Turn off LED 2
    digitalWrite(PIN_LED2, LOW);      
    //Turn on LED 3
    digitalWrite(PIN_LED3, HIGH);     
    //Wait 500ms
    delay(500);                      
    //Turn off LED 3
    digitalWrite(PIN_LED3, LOW);      
    //Turn on LED 4
    digitalWrite(PIN_LED4, HIGH);     
    //Wait 500ms
    delay(500);                      
    //Turn off LED 4
    digitalWrite(PIN_LED4, LOW);      
}
```

