// 7 Segment Display Counter
// This code interfaces with a circuit that has a 7 segment display
// The display counts from 0 to 9 every 500 milliseconds and then starts again.

// Segment pins on Digital I/O 2-9
const int segmentPins[] = {2, 3, 4, 5, 6, 7, 8, 9};

// Segment values for displaying numbers 0-9
const byte segmentValues[] = {
  0b00111111, // 0
  0b00000110, // 1
  0b01011011, // 2
  0b01001111, // 3
  0b01100110, // 4
  0b01101101, // 5
  0b01111101, // 6
  0b00000111, // 7
  0b01111111, // 8
  0b01101111  // 9
};

// the setup function runs once when you press reset or power the board
void setup() {
  // Initialize segment pins to output mode
  for (int i = 0; i < 8; i++) {
    pinMode(segmentPins[i], OUTPUT);
  }
}

// the loop function runs over and over again forever
void loop() {
  for (int i = 0; i < 10; i++) {
    displayNumber(i);
    delay(500); // wait for 500 milliseconds
  }
}

void displayNumber(int number) {
  byte value = segmentValues[number];
  for (int i = 0; i < 8; i++) {
    digitalWrite(segmentPins[i], (value >> i) & 1);
  }
}
```

