Arduino Uno Code:
```cpp
#include <Arduino.h>

// Stepper motor pins
#define ENABLE_PIN 8
#define STEP_PIN 9
#define DIR_PIN 10

// Stepper motor settings
#define STEPS_PER_REVOLUTION 200 // 1.8° step angle for NEMA 17 stepper motor
#define STEP_DELAY 1000 // Delay between steps in microseconds

// Oscillation settings
#define OSCILLATION_ANGLE 45
#define OSCILLATION_DELAY 5000 // Oscillation delay in milliseconds

// Global variables
int stepsToMove;
bool direction;

void setup() {
  // Set stepper motor pins to output mode
  pinMode(ENABLE_PIN, OUTPUT);
  pinMode(STEP_PIN, OUTPUT);
  pinMode(DIR_PIN, OUTPUT);

  // Calculate steps to move for oscillation angle
  stepsToMove = (OSCILLATION_ANGLE * STEPS_PER_REVOLUTION) / 360;

  // Initialize direction
  direction = true;
}

void loop() {
  // Set direction
  digitalWrite(DIR_PIN, direction);

  // Enable stepper motor driver
  digitalWrite(ENABLE_PIN, LOW);

  // Move stepper motor
  for (int i = 0; i < stepsToMove; i++) {
    digitalWrite(STEP_PIN, HIGH);
    delayMicroseconds(STEP_DELAY);
    digitalWrite(STEP_PIN, LOW);
    delayMicroseconds(STEP_DELAY);
  }

  // Disable stepper motor driver
  digitalWrite(ENABLE_PIN, HIGH);

  // Toggle direction
  direction = !direction;

  // Wait for oscillation delay
  delay(OSCILLATION_DELAY);
}
```

