#include <Bounce.h>
#include <Keyboard.h>

// LED on Digital I/O 0
#define PIN_LED 0
// Buzzer on Digital I/O 1
#define PIN_BUZZER 1

// Button pins
const int buttonPins[] = {2, 3, 4, 5, 6, 7, 8, 9, 10};
const int numButtons = sizeof(buttonPins) / sizeof(buttonPins[0]);

// ASCII codes for emojis
const char* emojis[] = {
    "❤️", "😊", "😢", "😂", "😡", "😮", "😎", "😴", "ABCDE"
};

// Bounce objects for buttons
Bounce buttons[numButtons];

// Initialize button states
int buttonStates[numButtons];

// Setup function
void setup() {
    pinMode(PIN_LED, OUTPUT);
    pinMode(PIN_BUZZER, OUTPUT);

    // Initialize buttons
    for (int i = 0; i < numButtons; i++) {
        pinMode(buttonPins[i], INPUT_PULLUP);
        buttons[i].attach(buttonPins[i]);
        buttons[i].interval(5);
    }

    // Start USB keyboard
    Keyboard.begin();
}

// Loop function
void loop() {
    // Check button states
    for (int i = 0; i < numButtons; i++) {
        buttons[i].update();
        if (buttons[i].fell()) {
            // Button pressed
            digitalWrite(PIN_LED, LOW); // Turn off LED
            Keyboard.print(emojis[i]);  // Send emoji
            playBuzzer(i);              // Play buzzer sound
            digitalWrite(PIN_LED, HIGH); // Turn on LED
        }
    }
}

// Function to play buzzer sound based on button index
void playBuzzer(int index) {
    int frequency = 0;
    int duration = 100;

    switch (index) {
        case 0: // Heart
            frequency = 523;
            break;
        case 1: // Happy
            frequency = 587;
            break;
        case 2: // Sad
            frequency = 440;
            break;
        case 3: // Laugh
            frequency = 659;
            break;
        case 4: // Angry
            frequency = 349;
            break;
        case 5: // Surprised
            frequency = 784;
            break;
        case 6: // Cool
            frequency = 988;
            break;
        case 7: // Sleepy
            frequency = 262;
            break;
        case 8: // Long string
            frequency = 880;
            break;
    }

    tone(PIN_BUZZER, frequency, duration);
    delay(duration);
}
