Teensy 4.0 Code:
```cpp
#include <Bounce.h>
#include <Audio.h>
#include <Wire.h>
#include <SPI.h>
#include <SD.h>
#include <SerialFlash.h>

// GUItool: begin automatically generated code
AudioSynthWaveformSine   sine1;          //xy=248,289
AudioOutputI2S           i2s1;           //xy=448,289
AudioConnection          patchCord1(sine1, 0, i2s1, 0);
AudioConnection          patchCord2(sine1, 0, i2s1, 1);
AudioControlSGTL5000     sgtl5000_1;     //xy=448,369
// GUItool: end automatically generated code

#define LED_PIN 9
#define BUZZER_PIN 10
#define NUM_BUTTONS 9
const int buttonPins[NUM_BUTTONS] = {0, 1, 2, 3, 4, 5, 6, 7, 8};
Bounce buttons[NUM_BUTTONS];

const char* emojiAscii[NUM_BUTTONS] = {
  "<3", // Heart
  ":)", // Happy
  ":(", // Sad
  ":D", // Laughing
  ":P", // Tongue out
  ";)", // Wink
  ":O", // Surprised
  ">:(", // Angry
  ":|", // Neutral
};

void setup() {
  AudioMemory(15);
  sgtl5000_1.enable();
  sgtl5000_1.volume(0.5);

  pinMode(LED_PIN, OUTPUT);
  digitalWrite(LED_PIN, HIGH);

  for (int i = 0; i < NUM_BUTTONS; i++) {
    pinMode(buttonPins[i], INPUT_PULLUP);
    buttons[i].attach(buttonPins[i]);
    buttons[i].interval(5);
  }
}

void loop() {
  for (int i = 0; i < NUM_BUTTONS; i++) {
    buttons[i].update();
    if (buttons[i].fell()) {
      digitalWrite(LED_PIN, LOW);
      delay(500);
      digitalWrite(LED_PIN, HIGH);
      playTune(i);
      Keyboard.print(emojiAscii[i]);
    }
  }
}

void playTune(int buttonIndex) {
  switch (buttonIndex) {
    case 0: // Heart
      sine1.frequency(440);
      sine1.amplitude(0.5);
      delay(200);
      sine1.amplitude(0);
      break;
    case 1: // Happy
      sine1.frequency(523);
      sine1.amplitude(0.5);
      delay(200);
      sine1.frequency(587);
      delay(200);
      sine1.amplitude(0);
      break;
    // Add more cases for other emojis
  }
}
```

