Arduino Uno Code:
```
#include <Wire.h>
#include <Adafruit_GFX.h>
#include <Adafruit_SSD1306.h>

// OLED display
#define SCREEN_WIDTH 128
#define SCREEN_HEIGHT 64
#define OLED_RESET -1
Adafruit_SSD1306 display(SCREEN_WIDTH, SCREEN_HEIGHT, &Wire, OLED_RESET);

// Spectrometer pins
#define PIN_EOS 2
#define PIN_START 3
#define PIN_CLK 4
#define PIN_GAIN 5
#define PIN_VIDEO A0

// Spectrometer settings
#define NUM_PIXELS 288
#define GAIN_VALUE 1

void setup() {
  pinMode(PIN_EOS, INPUT);
  pinMode(PIN_START, OUTPUT);
  pinMode(PIN_CLK, OUTPUT);
  pinMode(PIN_GAIN, OUTPUT);
  pinMode(PIN_VIDEO, INPUT);

  digitalWrite(PIN_GAIN, GAIN_VALUE);
  digitalWrite(PIN_START, LOW);
  digitalWrite(PIN_CLK, LOW);

  // Initialize OLED display
  if (!display.begin(SSD1306_SWITCHCAPVCC, 0x3C)) {
    for (;;); // Don't proceed, loop forever
  }
  display.clearDisplay();
  display.setTextSize(1);
  display.setTextColor(WHITE);
  display.setCursor(0, 0);
}

void loop() {
  uint16_t spectrum[NUM_PIXELS];

  // Start spectrometer measurement
  digitalWrite(PIN_START, HIGH);
  delayMicroseconds(50);
  digitalWrite(PIN_START, LOW);

  // Read spectrometer data
  for (int i = 0; i < NUM_PIXELS; i++) {
    digitalWrite(PIN_CLK, HIGH);
    delayMicroseconds(1);
    spectrum[i] = analogRead(PIN_VIDEO);
    digitalWrite(PIN_CLK, LOW);
    delayMicroseconds(1);
  }

  // Wait for EOS signal
  while (digitalRead(PIN_EOS) == LOW);

  // Display spectrum on OLED
  display.clearDisplay();
  for (int i = 0; i < NUM_PIXELS; i++) {
    int height = map(spectrum[i], 0, 1023, 0, SCREEN_HEIGHT);
    display.drawLine(i, SCREEN_HEIGHT - 1, i, SCREEN_HEIGHT - height - 1, WHITE);
  }
  display.display();
}
```

